import { createStore } from "redux";

const initialState = {
	things: [
	{
		name: "trust",
		guid: 4312
	}
  ]
};


function rootReducer(state, action) {
	console.log(action.type);
	switch (action.type) {
		default:
		  return state 
	}
}


export default function conofigureStore() {
	const store = createStore(rootReducer, initialState);
	return store;
}
